import React, { Component } from "react";
import "./tvshowdetail.css";
import Iframe from "react-iframe";
import 'materialize-css/dist/css/materialize.min.css';
import Slider from "react-slick";
const image = "https://image.tmdb.org/t/p/w500";


class TvshowDetail extends Component {
  state = {
    movieDe: [],
    gendre: [],
    video: [],
    playin: false
  };

  video = () => {
    const apikey = "9347ea3310e2b25bb6d5eab69e5caca8";
    const apiUrl = `https://api.themoviedb.org/3/tv/${
      this.props.match.params.tvshow
    }/videos?api_key=${apikey}`;

    fetch(apiUrl)
      .then(res => res.json())
      .then(video => {
        this.setState({ ...this.state, video: video.results });
      });
  };
  componentDidMount() {
    const apikey = "9347ea3310e2b25bb6d5eab69e5caca8";
    const apiUrl = `https://api.themoviedb.org/3/tv/${
      this.props.match.params.tvshow
    }?api_key=${apikey}`;
    fetch(apiUrl)
      .then(res => res.json())
      .then(data => {
        if (data.poster_path) {
          this.setState({ movieDe: data, gendre: data.genres });
          document.title = data.original_name;
        }
      });
    this.video();
  }
  render() {
    const { movieDe, gendre, video } = this.state;

    const trailerVid = video.map(vid => {
      const url = `https://www.youtube.com/embed/${vid.key}`;
      return (
        <div className="vieosIframe open-trailer" key={vid.id}>
          <Iframe
            url={url}
            title="youtube"
            width="100%"
            height="450px"
            frameborder="0"
            allow="accelerometer; autoplay; encrypted-media; gyroscope; picture-in-picture"
            allowFullScreen
          />
        </div>
      );
    });

    const gen = gendre.map(genre => (
      <div key={genre.id}>
        <span>genre: {genre.name}</span>
      </div>
    ));

    const poster = image + movieDe.poster_path;

    const trailerButton = () => {
      document.querySelector(".vieosIframe").classList.toggle("openTrailer");
      this.setState({ ...this.state, playin: true });
    };

    return (
      <div
        className="container"
        
      >
      <div className="row">
      <div className="col s4">
        <div className="poster " style={{wdith:"250px",height:"250px"}}>
          <img src={poster} alt="movie"  />
          
            <p>{movieDe.original_name}</p>
            </div>
<div className="">
            {gen}
            <span>Status: {movieDe.status}</span>
          </div>
          
          
        </div>
        {trailerVid[0]}
        </div>
      </div>
    );
  }
}

export default TvshowDetail;
