import React, { Component } from "react";
import "./homePage.scss";
import { connect } from "react-redux";
import 'materialize-css/dist/css/materialize.min.css';
import M from 'materialize-css';
import { SearchMovie1 } from "../redux/actions/actions";

const image = "https://image.tmdb.org/t/p/w500";

class Home extends Component {
  state = {
    movies: []
  };

  componentDidMount() {

   

    const apikey = "9347ea3310e2b25bb6d5eab69e5caca8";
    const apiUrl = `https://api.themoviedb.org/3/discover/movie?api_key=${apikey}`;

    fetch(apiUrl)
      .then(res => res.json())
      .then(data => {
        this.setState({
          movies: data.results
        });
      })
      .catch(err => console.log(err));
  }
  render() {
    const { movies } = this.state;

    return (
      <React.Fragment>
      <div className="container">
        <div class="row card grey darken-3" style={{padding:"10px"}}>
        <form className="container" onSubmit={this.handleSubmit}>
    <div class="col s12">
      <div class="row">
        <div class="input-field col s12">
          <i class="material-icons prefix fa fa-search "></i>
          <input type="text" onChange={this.handleChange} className="white-text" placeholder="Search Movies and Tv shows" />
          
        </div>
      </div>
    </div>
    </form>
  </div>
      </div>


      <div className="popular">
        {movies.map(movie => (
          <div key={movie.id} className="popularMovies white-text  ">
            
            <img
              src={image + movie.poster_path}
              alt=""
              onClick={() => this.props.history.push("/movie/" + movie.id)}
            />
            <p>{movie.original_title}</p>
          </div>
        ))}
        <p></p>
      </div>
      </React.Fragment>
    );
  }
}

const mapStateToProps = state => {
  return {
    allMovies: state.allstaff
  };
};

export default connect(mapStateToProps)(Home);
