import React, { Component } from "react";
import "./moviedetails.scss";
import Iframe from "react-iframe";
const image = "https://image.tmdb.org/t/p/w500";

class MovieDetails extends Component {
  state = {
    movieDe: [],
    gendre: [],
    video: []
  };

  trailerButton = () => {
    document.querySelector(".vieosIframe").classList.add("openTrailer");
  };

  trailerButton1 = () => {
    document.querySelector(".vieosIframe")
  };

  fetchVideo = () => {
    const apikey = "9347ea3310e2b25bb6d5eab69e5caca8";
    const apiUrl = `https://api.themoviedb.org/3/movie/${
      this.props.match.params.movieDetials
    }/videos?api_key=${apikey}`;
    fetch(apiUrl)
      .then(res => res.json())
      .then(data => {
        this.setState({ ...this.state, video: data.results });
      });
  };
  componentDidMount() {
    const apikey = "9347ea3310e2b25bb6d5eab69e5caca8";
    const apiUrl = `https://api.themoviedb.org/3/movie/${
      this.props.match.params.movieDetials
    }?api_key=${apikey}`;
    fetch(apiUrl)
      .then(res => res.json())
      .then(data => {
        if (data.poster_path) {
          this.setState({ movieDe: data, gendre: data.genres });
          document.title = data.original_title;
        }
      });
    this.fetchVideo();
  }
  render() {
    const { movieDe, gendre, video } = this.state;

    const gen = gendre.map(genre => (
      <div key={genre.id}>
        <span>genre: {genre.name}</span>
      </div>
    ));
    const poster = image + movieDe.poster_path;

    const returnVid = video.map(vid => {
      const url = `https://www.youtube.com/embed/${vid.key}`;
      return (
        <div className="vieosIframe" key={vid.id}>
          <Iframe
            src={url}
            width="100%"
            height="450px"
            frameborder="0"
            allow="accelerometer; autoplay; encrypted-media; gyroscope; picture-in-picture"
            allowFullScreen
          />
        </div>
      );
    });

    return poster ? (
      <div
        className="movieDetail"
        style={{ backgroundImage: `url(${poster})` }}
      >
        <div className="poster">
          <img src={poster} alt="movie" />
          <div className="details">
            <h1>{movieDe.original_title}</h1>
            {gen}
            <span>Released: {movieDe.release_date}</span>
            <span>Status: {movieDe.status}</span>
            <button className="watch" onClick={this.trailerButton}>
              close trailer
            </button>
            <button className="watch" onClick={this.trailerButton1}>
              open trailer
            </button>
          </div>
        </div>
        {returnVid}
      </div>
    ) : null;
  }
}

export default MovieDetails;
